mapper.select:
SELECT COUNT(*) FROM car_info


			AND CI_NUM=#{ciNum}


			AND CI_NAME LIKE CONCAT('%',#{ciName},'%')


			AND CI_YEAR = #{ciYear}

SELECT CI_NUM, CI_NAME, CI_YEAR FROM CAR_INFO


		AND CI_NUM=#{ciNum}


		AND CI_NAME LIKE CONCAT('%',#{ciName},'%')


		AND CI_YEAR = #{ciYear}


ORDER BY CI_NUM DESC
LIMIT #{start}, #{count}

mapper.update:
UPDATE CAR_INFO
SET CI_NAME=#{ciName},
CI_YEAR = #{ciYear}
WHERE CI_NUM = #{ciNum}
